import csv
import datetime
import linecache
import string

import pytest
import requests
import json
from pytest_csv_params.decorator import csv_params


@csv_params(
    data_file="../Staff/token_staff.csv",
    id_col=None,
    data_casts={
        "staff_id": str,
        "token": str,
    },
)
def test_staff_stamp(staff_id, token):
    current_date = datetime.date.today().isoformat()
    with open('C:/Users/Nhan.dt/PycharmProjects/TestPy/Commont values/shop_id.csv', 'r') as file:
        reader = csv.reader(file)
        first_row = next(reader)  # Lấy hàng đầu tiên từ tệp CSV
        shop_id = first_row[0]  # Lấy giá trị từ cột thích hợp trong hàng đầu tiên

        url = f"https://kintai.stg.jinjer.net/v1//dashboard/shops/{shop_id}/time_cards"

        payload = json.dumps({
            "comment": "",
            "date": current_date,
            "isbeacon": "0",
            "latitude": "37.785835",
            "longitude": "-122.406418",
            "night_mode": "0",
            "smiling": "0",
            "image": "",
            "type": "check_in",
            "type_swipe": "スマホアプリ"
        })
        headers = {
            'User-Agent': 'iPhone',
            'Api-Token': token,
            'Content-Type': 'application/json'
        }

        resp = requests.request("POST", url, headers=headers, data=payload)
        resp_body = resp.json()
        print(resp_body['message'])
        assert resp_body['code'] == 200
        print(resp_body['message'])
